// Enterprise GitHub Activity Intelligence Engine
const commits = $input.first().json;
const repoData = $input.all().find(item => item.json.full_name)?.json || {};
const languages = $input.all().find(item => !Array.isArray(item.json) && !item.json.full_name)?.json || {};

// Advanced commit analysis
function analyzeCommits(commits) {
  const analysis = {
    totalCommits: commits.length,
    commitMessages: commits.map(c => c.commit.message),
    authors: [...new Set(commits.map(c => c.commit.author.name))],
    dates: commits.map(c => c.commit.author.date),
    complexity: 'basic'
  };

  // Technical skill extraction with enterprise patterns
  const skillPatterns = {
    ai: /\b(ai|artificial intelligence|machine learning|ml|neural|tensorflow|pytorch|scikit|keras|transformers|llm|gpt|bert|nlp|computer vision|deep learning)\b/gi,
    automation: /\b(automation|workflow|pipeline|ci\/cd|jenkins|github actions|gitlab ci|docker|kubernetes|k8s|terraform|ansible|chef|puppet)\b/gi,
    cloud: /\b(aws|azure|gcp|cloud|serverless|lambda|microservices|api gateway|load balancer|cdn)\b/gi,
    frontend: /\b(react|vue|angular|javascript|typescript|html|css|sass|webpack|vite|next\.js|nuxt)\b/gi,
    backend: /\b(node|python|java|go|rust|django|flask|express|fastapi|spring|gin|actix)\b/gi,
    database: /\b(sql|nosql|mongodb|postgresql|mysql|redis|elasticsearch|dynamodb|cassandra)\b/gi,
    devops: /\b(devops|sre|monitoring|logging|prometheus|grafana|elk|observability|security|testing)\b/gi,
    architecture: /\b(microservices|monolith|event-driven|cqrs|saga|ddd|clean architecture|hexagonal)\b/gi
  };

  const detectedSkills = {};
  const allText = analysis.commitMessages.join(' ').toLowerCase();

  Object.keys(skillPatterns).forEach(skill => {
    const matches = allText.match(skillPatterns[skill]) || [];
    if (matches.length > 0) {
      detectedSkills[skill] = [...new Set(matches)];
    }
  });

  // Complexity assessment
  if (commits.length > 20 || Object.keys(detectedSkills).length > 4) {
    analysis.complexity = 'advanced';
  } else if (commits.length > 10 || Object.keys(detectedSkills).length > 2) {
    analysis.complexity = 'intermediate';
  }

  // Achievement recognition
  const achievements = [];
  const fixPattern = /\b(fix|bug|issue|resolve|patch)\b/gi;
  const featurePattern = /\b(feature|add|implement|create|build)\b/gi;
  const optimizePattern = /\b(optimize|improve|enhance|refactor|performance)\b/gi;

  if (allText.match(fixPattern)) achievements.push('Bug fixes and debugging');
  if (allText.match(featurePattern)) achievements.push('Feature development');
  if (allText.match(optimizePattern)) achievements.push('Code optimization');

  // Business impact calculation
  const businessImpact = {
    codeQuality: Math.min(100, commits.length * 2 + Object.keys(detectedSkills).length * 10),
    innovation: Object.keys(detectedSkills).includes('ai') ? 90 : 70,
    collaboration: analysis.authors.length > 1 ? 85 : 60,
    productivity: Math.min(100, commits.length * 3)
  };

  return {
    ...analysis,
    detectedSkills,
    achievements,
    businessImpact,
    repositoryMetrics: {
      name: repoData.name || 'Unknown',
      description: repoData.description || '',
      stars: repoData.stargazers_count || 0,
      forks: repoData.forks_count || 0,
      size: repoData.size || 0,
      primaryLanguage: repoData.language || 'Unknown'
    },
    languageBreakdown: languages,
    innovationIndex: Math.round((businessImpact.innovation + businessImpact.codeQuality) / 2)
  };
}

// Market trend analysis
function getTrendingTopics() {
  const currentDate = new Date();
  const trends = {
    'Q1': ['AI Automation', 'Cloud Native', 'DevSecOps', 'Edge Computing'],
    'Q2': ['Machine Learning Operations', 'Serverless Architecture', 'API-First Development'],
    'Q3': ['Digital Transformation', 'Microservices', 'Data Engineering', 'Platform Engineering'],
    'Q4': ['AI Ethics', 'Sustainable Computing', 'Zero Trust Security', 'Observability']
  };

  const quarter = 'Q' + Math.ceil((currentDate.getMonth() + 1) / 3);
  return trends[quarter] || trends['Q1'];
}

const analysis = analyzeCommits(commits);
analysis.trendingTopics = getTrendingTopics();
analysis.analysisDate = new Date().toISOString();
analysis.hasActivity = analysis.totalCommits > 0;

return { json: analysis };

// Enterprise Content Strategy Planner
const analysis = $input.first().json;
const config = $('enterprise-config').first().json;

// Content strategy engine
function planContentStrategy(analysis, userConfig) {
  const strategy = {
    contentType: determineContentType(analysis),
    targetAudience: determineAudience(analysis, userConfig),
    keyMessages: extractKeyMessages(analysis),
    hashtagStrategy: generateHashtagStrategy(analysis, userConfig),
    toneProfile: userConfig.content_tone || 'technical-executive',
    urgency: calculateUrgency(analysis),
    viralPotential: assessViralPotential(analysis)
  };

  return strategy;
}

function determineContentType(analysis) {
  if (analysis.innovationIndex > 80) return 'thought_leadership';
  if (analysis.detectedSkills.ai) return 'technical_breakthrough';
  if (analysis.totalCommits > 15) return 'productivity_showcase';
  if (analysis.achievements.length > 2) return 'achievement_highlight';
  return 'technical_update';
}

function determineAudience(analysis, config) {
  const audiences = [];

  if (analysis.detectedSkills.ai || analysis.detectedSkills.automation) {
    audiences.push('AI Engineers', 'CTOs', 'Tech Leaders');
  }
  if (analysis.detectedSkills.devops || analysis.detectedSkills.cloud) {
    audiences.push('DevOps Engineers', 'Platform Engineers', 'SREs');
  }
  if (analysis.businessImpact.innovation > 80) {
    audiences.push('Innovation Leaders', 'VPs of Engineering');
  }

  return audiences.length > 0 ? audiences : ['Software Engineers', 'Developers'];
}

function extractKeyMessages(analysis) {
  const messages = [];

  if (analysis.totalCommits > 20) {
    messages.push(`Delivered ${analysis.totalCommits} commits in one week`);
  }

  Object.keys(analysis.detectedSkills).forEach(skill => {
    const skillMap = {
      ai: 'Advanced AI/ML implementation',
      automation: 'Workflow automation excellence',
      cloud: 'Cloud-native architecture',
      devops: 'DevOps best practices'
    };
    if (skillMap[skill]) messages.push(skillMap[skill]);
  });

  if (analysis.innovationIndex > 85) {
    messages.push('Cutting-edge innovation in software development');
  }

  return messages;
}

function generateHashtagStrategy(analysis, config) {
  const hashtags = ['#TechLeadership', '#SoftwareEngineering', '#Innovation'];

  // Industry-specific hashtags
  const industries = (config.user_industries || '').split(',');
  industries.forEach(industry => {
    const industryMap = {
      fintech: '#FinTech',
      ai: '#ArtificialIntelligence',
      automation: '#Automation',
      devops: '#DevOps'
    };
    if (industryMap[industry.trim()]) {
      hashtags.push(industryMap[industry.trim()]);
    }
  });

  // Skill-based hashtags
  Object.keys(analysis.detectedSkills).forEach(skill => {
    const skillHashtags = {
      ai: ['#MachineLearning', '#AI'],
      automation: ['#WorkflowAutomation', '#CI_CD'],
      cloud: ['#CloudNative', '#AWS'],
      frontend: ['#Frontend', '#React'],
      backend: ['#Backend', '#API'],
      devops: ['#DevOps', '#Infrastructure']
    };
    if (skillHashtags[skill]) {
      hashtags.push(...skillHashtags[skill]);
    }
  });

  return [...new Set(hashtags)].slice(0, 10); // Limit to 10 hashtags
}

function calculateUrgency(analysis) {
  let urgency = 'medium';

  if (analysis.innovationIndex > 90 || analysis.totalCommits > 25) {
    urgency = 'high';
  } else if (analysis.totalCommits < 5) {
    urgency = 'low';
  }

  return urgency;
}

function assessViralPotential(analysis) {
  let score = 50; // Base score

  // AI/ML content tends to be more engaging
  if (analysis.detectedSkills.ai) score += 20;

  // High productivity is impressive
  if (analysis.totalCommits > 20) score += 15;

  // Innovation drives engagement
  if (analysis.innovationIndex > 85) score += 15;

  // Multiple skills show expertise
  score += Object.keys(analysis.detectedSkills).length * 3;

  return Math.min(100, score);
}

const strategy = planContentStrategy(analysis, config);

return {
  json: {
    ...analysis,
    contentStrategy: strategy,
    readyForPreview: true,
    previewScheduled: new Date(Date.now() + 3 * 24 * 60 * 60 * 1000).toISOString() // 3 days from now
  }
};

// Enterprise Content Quality Assurance & Selection Engine
const inputs = $input.all();

// Extract generated content from different AI models
const gptContent = inputs.find(item => item.json.choices)?.json.choices[0]?.message?.content || '';
const claudeContent = inputs.find(item => item.json.content && item.json.content[0])?.json.content[0]?.text || '';
const geminiContent = inputs.find(item => item.json.candidates)?.json.candidates[0]?.content?.parts[0]?.text || '';

const analysisData = $('Enterprise Content Strategist').first().json;

// Content evaluation criteria
function evaluateContent(content, criteria) {
  let score = 0;

  // Length optimization (LinkedIn sweet spot: 1300-1900 characters)
  const length = content.length;
  if (length >= 1300 && length <= 1900) score += 20;
  else if (length >= 1000 && length <= 2200) score += 15;
  else score += 5;

  // Hook strength (compelling opening)
  const openingLines = content.split('\n')[0];
  if (openingLines.includes('üöÄ') || openingLines.includes('üí°') || openingLines.includes('‚ö°')) score += 15;
  if (/\d+%|\d+ [a-zA-Z]+|\$\d+/.test(openingLines)) score += 15; // Numbers/metrics in opening

  // Technical authority indicators
  const techTerms = ['AI', 'automation', 'architecture', 'pipeline', 'deployment', 'optimization', 'scalability'];
  const techScore = techTerms.filter(term => content.toLowerCase().includes(term.toLowerCase())).length;
  score += Math.min(20, techScore * 3);

  // Business impact language
  const businessTerms = ['impact', 'results', 'performance', 'efficiency', 'ROI', 'transformation', 'innovation'];
  const businessScore = businessTerms.filter(term => content.toLowerCase().includes(term.toLowerCase())).length;
  score += Math.min(15, businessScore * 2);

  // Professional formatting
  const paragraphs = content.split('\n\n').length;
  if (paragraphs >= 3 && paragraphs <= 6) score += 10;

  // Hashtag presence and quality
  const hashtagCount = (content.match(/#\w+/g) || []).length;
  if (hashtagCount >= 5 && hashtagCount <= 10) score += 10;

  // Call-to-action presence
  const ctaIndicators = ['What', 'How', 'thoughts', 'experience', 'connect', 'discuss', 'share'];
  if (ctaIndicators.some(cta => content.toLowerCase().includes(cta.toLowerCase()))) score += 10;

  return Math.min(100, score);
}

// Evaluate all generated content
const contentOptions = [
  { source: 'GPT-4', content: gptContent, model: 'primary' },
  { source: 'Claude', content: claudeContent, model: 'backup' },
  { source: 'Gemini', content: geminiContent, model: 'specialist' }
].filter(option => option.content && option.content.length > 100);

// Score each option
const scoredContent = contentOptions.map(option => ({
  ...option,
  qualityScore: evaluateContent(option.content, analysisData.contentStrategy),
  characterCount: option.content.length,
  hashtagCount: (option.content.match(/#\w+/g) || []).length,
  hasMetrics: /\d+%|\d+x|\$\d+|\d+ [a-zA-Z]+/.test(option.content)
}));

// Select best content or create hybrid
let selectedContent;
if (scoredContent.length === 0) {
  // Fallback content generation
  selectedContent = {
    source: 'Fallback',
    content: generateFallbackContent(analysisData),
    qualityScore: 75,
    model: 'fallback'
  };
} else {
  // Sort by quality score and select the best
  scoredContent.sort((a, b) => b.qualityScore - a.qualityScore);
  selectedContent = scoredContent[0];

  // If top scores are close, create hybrid content
  if (scoredContent.length > 1 && scoredContent[0].qualityScore - scoredContent[1].qualityScore < 10) {
    selectedContent = createHybridContent(scoredContent[0], scoredContent[1], analysisData);
  }
}

// Generate fallback content if AI models fail
function generateFallbackContent(analysis) {
  const skills = Object.keys(analysis.detectedSkills).join(', ');
  const commits = analysis.totalCommits;
  const innovation = analysis.innovationIndex;

  return `üöÄ Just wrapped up an incredible week of development!

Delivered ${commits} commits focused on ${skills} - pushing the boundaries of what's possible in software engineering.

Key highlights:
‚Ä¢ Innovation index: ${innovation}/100
‚Ä¢ ${analysis.achievements.join('\n‚Ä¢ ')}
‚Ä¢ Driving technical excellence across our platform

The intersection of automation and AI continues to reshape how we build scalable solutions. Every commit is a step toward more intelligent, efficient systems.

What challenges are you solving with automation in your stack?

${analysis.contentStrategy.hashtagStrategy.slice(0, 8).join(' ')}`;
}

// Create hybrid content combining best elements
function createHybridContent(content1, content2, analysis) {
  // Use the better hook from content1, body from content2, or vice versa
  const lines1 = content1.content.split('\n');
  const lines2 = content2.content.split('\n');

  // Take the stronger opening
  const hook = content1.qualityScore > content2.qualityScore ? lines1[0] : lines2[0];

  // Combine body content, removing duplicates
  const body1 = lines1.slice(1).join('\n');
  const body2 = lines2.slice(1).join('\n');

  const hybrid = `${hook}\n\n${body1.length > body2.length ? body1 : body2}`;

  return {
    source: 'Hybrid AI',
    content: hybrid,
    qualityScore: Math.max(content1.qualityScore, content2.qualityScore) + 5,
    model: 'hybrid',
    hybridOf: [content1.source, content2.source]
  };
}

// Content optimization and formatting
function optimizeContent(content, analysis) {
  let optimized = content;

  // Ensure proper spacing
  optimized = optimized.replace(/\n{3,}/g, '\n\n');

  // Ensure hashtags are at the end
  const hashtags = analysis.contentStrategy.hashtagStrategy.slice(0, 8).join(' ');
  if (!optimized.includes('#')) {
    optimized += `\n\n${hashtags}`;
  }

  // Add professional emoji if missing
  if (!optimized.match(/[üöÄüí°‚ö°üéØüìàüî•‚ú®]/)) {
    optimized = 'üöÄ ' + optimized;
  }

  return optimized;
}

// Final content preparation
const finalContent = optimizeContent(selectedContent.content, analysisData);

// Performance tracking data
const performanceData = {
  contentGenerated: new Date().toISOString(),
  modelUsed: selectedContent.source,
  qualityScore: selectedContent.qualityScore,
  characterCount: finalContent.length,
  expectedEngagement: analysisData.contentStrategy.viralPotential,
  targetAudience: analysisData.contentStrategy.targetAudience,
  contentType: analysisData.contentStrategy.contentType,
  hashtagCount: (finalContent.match(/#\w+/g) || []).length
};

return {
  json: {
    ...analysisData,
    selectedContent: {
      text: finalContent,
      source: selectedContent.source,
      model: selectedContent.model,
      qualityScore: selectedContent.qualityScore
    },
    allGeneratedOptions: scoredContent,
    performanceData,
    readyToPost: true,
    scheduledPostTime: new Date().toISOString()
  }
};

// Enterprise Analytics & Performance Tracking
const publishResponse = $input.first().json;
const contentData = $('Content QA & Selector').first().json;
const config = $('enterprise-config').first().json;

// Extract LinkedIn post ID from response
const postId = publishResponse.id || publishResponse.urn || 'unknown';
const postUrl = publishResponse.id ? `https://www.linkedin.com/feed/update/${postId}/` : 'Not available';

// Performance metrics calculation
const performanceMetrics = {
  postId: postId,
  postUrl: postUrl,
  publishedAt: new Date().toISOString(),
  contentLength: contentData.selectedContent.text.length,
  hashtagCount: (contentData.selectedContent.text.match(/#\w+/g) || []).length,
  qualityScore: contentData.selectedContent.qualityScore,
  aiModel: contentData.selectedContent.source,
  expectedEngagement: contentData.contentStrategy.viralPotential,
  targetReach: calculateExpectedReach(contentData),
  businessImpact: contentData.businessImpact,
  technicalSkills: Object.keys(contentData.detectedSkills),
  contentStrategy: contentData.contentStrategy.contentType
};

// Calculate expected reach based on content quality and user profile
function calculateExpectedReach(data) {
  let baseReach = 1000; // Base reach for professional content

  // Quality multiplier
  baseReach *= (data.selectedContent.qualityScore / 100) + 0.5;

  // Viral potential multiplier
  baseReach *= (data.contentStrategy.viralPotential / 100) + 0.3;

  // Technical content tends to have more engaged but smaller audience
  if (data.contentStrategy.contentType === 'technical_deep_dive') {
    baseReach *= 0.8;
  } else if (data.contentStrategy.contentType === 'thought_leadership') {
    baseReach *= 1.5;
  }

  return Math.round(baseReach);
}

// Business impact calculation
const businessImpact = {
  brandAwareness: {
    estimatedImpressions: performanceMetrics.targetReach * 2.5,
    professionalReach: Math.round(performanceMetrics.targetReach * 0.85),
    industryInfluence: contentData.innovationIndex > 80 ? 'High' : 'Medium'
  },
  leadGeneration: {
    expectedConnections: Math.round(performanceMetrics.targetReach * 0.02),
    qualifiedProspects: Math.round(performanceMetrics.targetReach * 0.005),
    estimatedValue: (performanceMetrics.targetReach * 0.005 * 2500).toLocaleString()
  },
  thoughtLeadership: {
    expertiseDisplay: contentData.detectedSkills,
    innovationShowcase: contentData.innovationIndex,
    industryPositioning: contentData.contentStrategy.targetAudience
  }
};

// Success notification data preparation
const successData = {
  status: 'SUCCESS',
  publishedAt: performanceMetrics.publishedAt,
  postUrl: postUrl,
  metrics: performanceMetrics,
  businessImpact: businessImpact,
  contentPreview: contentData.selectedContent.text.substring(0, 200) + '...',
  analyticsAvailable: true,
  nextAnalysisDate: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString() // Next week
};

// Store performance data for future analysis
const analyticsData = {
  timestamp: new Date().toISOString(),
  organizationId: config.organization_id,
  userId: config.github_username,
  postData: {
    id: postId,
    content: contentData.selectedContent.text,
    contentType: contentData.contentStrategy.contentType,
    aiModel: contentData.selectedContent.source,
    qualityScore: contentData.selectedContent.qualityScore
  },
  githubActivity: {
    commits: contentData.totalCommits,
    skills: contentData.detectedSkills,
    innovationIndex: contentData.innovationIndex,
    repositoryMetrics: contentData.repositoryMetrics
  },
  predictedMetrics: performanceMetrics,
  businessProjections: businessImpact
};

return {
  json: {
    ...successData,
    analyticsData: analyticsData,
    webhookData: {
      event: 'linkedin_post_published',
      timestamp: new Date().toISOString(),
      data: successData
    }
  }
};

// Low Activity Workflow Handler
const analysis = $input.first().json;
const config = $('enterprise-config').first().json;

// Generate minimal activity report
const lowActivityReport = {
  status: 'LOW_ACTIVITY',
  period: 'Last 7 days',
  commitCount: analysis.totalCommits || 0,
  reason: analysis.totalCommits === 0 ? 'No commits found' : 'Insufficient activity for content generation',
  suggestions: [
    'Increase development activity',
    'Consider manual content creation',
    'Review repository settings',
    'Check if commits are being tracked correctly'
  ],
  nextCheck: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString(),
  alternativeActions: [
    'Share industry insights',
    'Post about learning goals',
    'Highlight team achievements',
    'Discuss technical challenges'
  ]
};

return { json: lowActivityReport };
